<template>
  <t-dialog
    v-model:visible="visibleModal.data"
    header="切换设备"
    mode="modal"
    draggable
    :footer="false"
    top="24px"
  >
    <template #body>
      <t-loading :loading="loading">
        <div class="flex justify-between mb-2 space-x-2">
          <t-button>
            <t-icon name="add" />
            创建新设备
          </t-button>
          <t-button @click="handleRefresh" variant="outline">
            <t-icon name="refresh" />
            刷新
          </t-button>
        </div>
        <t-list :split="true" style="max-height: 65vh;">
          <t-list-item v-for="item in listData.data" :key="item.ID">
            <t-list-item-meta :title="item.Name" :description="item.ApiTag" image="data:image/svg+xml;base64," />
            <template #action>
              <t-button variant="outline">选择</t-button>
            </template>
          </t-list-item>
        </t-list>
      </t-loading>
    </template>
  </t-dialog>
</template>

<script setup>
import {onBeforeUnmount, onMounted, reactive, ref, watch} from 'vue'
import api from "../../../../api/index.js";

const props = defineProps({
  show: Object,
  DeviceID: Object
})

const loading = ref(false)
const visibleModal = reactive(props.show)
const listData = reactive({data: []})

watch(visibleModal, async (v) => {
  if (v.data === true) {
    await handleRefresh()
  }
})

function handleClose() {
  visibleModal.data = false
}

async function handleRefresh() {
  if (!loading.value) {
    loading.value = true

    const data = await api.device.getDeviceInfo({ID: -1, Page:1, PageSize: 5})
    if (data) {
      listData.data = data.data
    }

    loading.value = false
  }
}

onMounted(async () => {

})

onBeforeUnmount(() => {

})
</script>

